put 'look';while line = get;if line =~ /a wooden cab which is riding the wooden trellis/;put 'open dam';put 'go cab';break;elsif line =~ /is currently running under an open wooden dam/;put 'close dam';echo '*******************';echo 'Waiting for the cab to arrive...';echo '******************';elsif line =~ /the stream is presently being diverted by a closed wooden dam/;echo '*******************';echo 'Someone already closed the dam, good!';echo 'Waiting for the cab to arrive...';echo '******************';elsif line =~ /A wooden cab, riding on the trellis, crawls down the side of the mountain and comes to a clunky stop/;put 'look';end;end